$(document).ready(function(){$.simpleWeather({location:"Spokane, WA",woeid:"",unit:"f",success:function(t){$(".city").html(t.city),$(".temp").html(t.temp),t.temp<50?$("body").addClass("cold"):t.temp>50?$("body").addClass("hot"):$("body").addClass("black"),$(".temp").html(t.temp),$(".cond-code").html(t.currently)},error:function(t){$("#weather").html("<p>"+t+"</p>")}})});